mapper.select:
select acc.account_line_id as accountLineId, acc.account_id as accountId, acc.account_type as accountType, acc.change_money as changeMoney, acc.change_time as changeTime, acc.pay_type as payType, acc.change_user_id as changeUserId, u.nickname as changeUserName from account_line acc left join user u on acc.change_user_id = u.id
acc.account_id = #{accountId}
order by acc.change_time desc

mapper.update:
update account_line set
    change_money = #{changeMoney},
    change_time = #{changeTime}
where
    account_line_id = #{accountLineId}

mapper.delete:
delete from account_line


        and account_line_id = #{accountLineId}


        and account_id = #{accountId}
