mapper.select:
select account_line_id as accountLineId, account_id as accountId, account_type as accountType, change_money as changeMoney, change_time as changeTime, change_user_id as changeUserId, pay_type as payType from account_line
account_id = #{accountId}
order by change_time desc

mapper.update:
update account_line set
    change_money = #{changeMoney},
    change_time = #{changeTime}
where
    account_line_id = #{accountLineId}

mapper.delete:
delete from account_line


        and account_line_id = #{accountLineId}


        and account_id = #{accountId}
